INSERT INTO `answers` VALUES ('271', '139', '1', '最核心的是选导师。不是选学校。\n\n推荐几个做图像领域的公司和研究院：\n百度深度学习研究院  \n腾讯\n阿里巴巴 \n中科院自动化所\n微软亚洲研究院\n索尼中国研究院\n虹软公司（南京+杭州  总部在硅谷）\n南京三星电子中国研发中心\n还有一些创业公司', '2015-04-20 21:21:18', null, null, null, '0');
