INSERT INTO `answers` VALUES ('2276', '522', '1', '小菜一碟，装次B＝＝\n{{{\n#!/usr/bin/env python \n# -*- coding: utf-8 -*- \nimport urllib  \nimport httplib2  \nimport re\nimport string, urllib2 \n\nfilew1 = open(\'/路径/aa.txt\', \'a\')\n  \nhttp = httplib2.Http()  \nurl = \'http://mm.taobao.com/json/request_top_list.htm?type=0&amp;page=\'\n\npattern1 = re.compile(r\'http://img\\d{2}.taobaocdn.com/sns_logo/i\\d/[^\\s?!_]*\')\n\ni = 1\nj = 1\nwhile i&lt;=5:\n	url1 = url + str(i) \n	i = i + 1\n	response, content = http.request(url1)\n	lianjie = re.findall(pattern1,content)\n	print lianjie\n	for lj in lianjie:\n		filew1.write(lj+\'\\n\')\n\nfiler1 = open(\'路径/aa.txt\', \'r\')\nfor line in filer1.readlines():\n	line = line.strip(\'\\n\')\n	print line\n	response, content = http.request(line)\n	taobaomeilj = str(j)+\'.jpg\'\n	j = j + 1\n	filew2 = open(\'image/\'+taobaomeilj, \'w\')\n	filew2.write(content)\n	filew2.close()\n}}}\n\n[attach]522[/attach]', '2015-04-20 21:21:18', null, null, null, '2');
